import * as React from "react";
import * as ReactDOM from "react-dom";

import { Provider } from "react-redux";
import { createStore, applyMiddleware, combineReducers } from "redux";
import thunk from "redux-thunk";

import rootReducer from "@physicsisbeautiful/pib-eval-library/lib/redux/modules/index";

import App from "./app";

const store = createStore(combineReducers(rootReducer), applyMiddleware(thunk));

ReactDOM.render(
  <Provider store={store}>
    <App />
  </Provider>,
  document.getElementById("root") as HTMLElement
);

import React from "react";

import Switch from "@material-ui/core/Switch";
import FormControlLabel from "@material-ui/core/FormControlLabel";

import Choices from "@physicsisbeautiful/pib-eval-library/lib/components/qa";
import { theme } from "@physicsisbeautiful/pib-eval-library/lib/components/style";
import { ThemeProvider } from "@material-ui/styles";

const App: React.FC = () => {
  // const textInput = createRef<HTMLInputElement>();
  // function addTodo(e: React.KeyboardEvent<HTMLInputElement>): void {
  //
  // }

  const [state, setState] = React.useState({
    checkedEditMode: false
  });

  const handleEditModeChange = () => (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setState({ ...state, checkedEditMode: event.target.checked });
  };

  return (
    <ThemeProvider theme={theme}>
      <div>
        <h1>Choices</h1>
        <FormControlLabel
          control={
            <Switch
              checked={state.checkedEditMode}
              color="primary"
              onChange={handleEditModeChange()}
              value=""
            />
          }
          label="Edit Mode"
        />
        <Choices editMode={state.checkedEditMode} />
      </div>
    </ThemeProvider>
  );
};

export default App;
